import {Button, CardMedia, Grid, Typography,} from "@mui/material";
import '../App.css'
import React, {useState} from "react";
import Box from '@mui/material/Box';
import basquiaPulito from "../imgs/logo_CivicLife.png"
import {CssTextField} from "../Utils/CustomTextFields";
import {Navigate, useLocation, useNavigate, useParams} from "react-router-dom";



const InitiativeDetails = () => {

    const location= useLocation()

    const isAdmin= location.state?.isAdmin
    const token= location.state?.token
    const loginType = location.state?.loginType
    const {initiative}= useParams()
    const [value, setValue] = useState(initiative)


    if (isAdmin === undefined || token === undefined || loginType === undefined) {
        console.log('non sei loggato')
        return <Navigate to='/'/>
    }

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setValue(event.target.value)
    }

    return (
        <>
            <Grid item xs={12} display="flex" justifyContent="center" alignItems="center" sx={{
                width: '100%',
                position: 'fixed',
                margin: "auto",
                top: 65,
                right: 0
            }}>
                <Typography
                    style={{color: '#feac0d', textAlign: 'center', fontSize: '3rem'}}>{initiative}</Typography>
            </Grid><Grid item xs={12} display="flex" justifyContent="center" alignItems="center" sx={{
            width: '100%', position: 'fixed',
            margin: "auto",
            top: 200, right: 0
        }}>
            <Box sx={{width: '70%', height: '100%', border: '2.5px solid #feac0d',}}>
                {/* TODO: se è il proprietario della iniziativa, allora può modificare il testo, altrimenti no --> check da fare poi sull'oggetto iniziativa*/}
                <CssTextField
                    sx={{backgroundColor: 'white'}}
                    value={value}
                    maxRows={15}
                    onChange={handleChange}
                    multiline/>
            </Box>

        </Grid><Grid item xs={12} display="flex" justifyContent='center' alignItems="right">
            {/* TODO: stessa cosa qui se è proprietaio della iniziativa non deve vedere il bottone*/}
            {
                isAdmin ?
                    <Button style={{
                        borderRadius: 35,
                        backgroundColor: "red",
                        padding: "10px 20px",
                        fontSize: "18px",
                        position: 'fixed',
                        margin: "auto",
                        top: 650,
                    }}
                            variant="contained">
                        Elimina
                    </Button>
                    :
                    <Button style={{
                        borderRadius: 35,
                        backgroundColor: "#92d36e",
                        padding: "10px 20px",
                        fontSize: "18px",
                        position: 'fixed',
                        margin: "auto",
                        top: 650,
                    }}
                            variant="contained">
                        Partecipa
                    </Button>
            }

        </Grid>
        </>
    );
}

export default InitiativeDetails;
