import {Grid, Typography} from "@mui/material";
import React, {useState} from "react";
import {CssTextField} from "../Utils/CustomTextFields";
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';

const InitiativeCreateForm = () => {
    const types = ['tipo1', 'tipo2', 'tipo3']
    const [usersList, setUsersList] = useState<any[]>(['utente1', 'utente2', 'utente3', 'utente4', 'utente5', 'utente6', 'utente7', 'utente8', 'utente9', 'utente10', 'utente11', 'utente12'])
    const [selectedUsers, setSelectedUsers] = useState<any>([])
    const [type, setType] = React.useState(types[0]);

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setType(event.target.value);
    };


    const handleDelete = (chipToDelete: any) => () => {
        setSelectedUsers((chips: any[]) => chips.filter((chip) => chip.key !== chipToDelete.key));
    };

    return (

        <Grid container sx={{position: 'fixed', bottom: 950}}>
            {/*    <Grid item xs={12} display="flex" sx={{*/}
            {/*    width: '100%',*/}
            {/*    margin: "auto",*/}
            {/*    top: 0,*/}
            {/*    right: 0*/}
            {/*}}>*/}
            {/*    <IconButton><KeyboardBackspaceIcon sx={{fontSize: 60, color: '#ffffff'}}/></IconButton>*/}
            {/*</Grid>*/}
            <Grid item xs={12} display="flex" justifyContent="center" alignItems="center" sx={{
                width: '100%',
                margin: "auto",
                position: 'fixed',
                top: 20
            }}>
                <Typography style={{color: '#feac0d', textAlign: 'center', fontSize: '3rem'}}>Crea la tua
                    iniziativa</Typography>
            </Grid>
            <Grid container direction='row' spacing={2} sx={{position: 'fixed', top: 150, bottom: 950}}>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <CssTextField sx={{input: {color: 'white'}, style: {color: 'white'}, width: '70%'}}
                                  label={'Nome'}/>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <CssTextField sx={{input: {color: 'white'}, style: {color: 'white'}, width: '70%'}}
                                  label={'Descrizione'}/>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <CssTextField sx={{input: {color: 'white'}, style: {color: 'white'}, width: '70%'}}
                                  label={'Luogo'}/>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <FormControl sx={{width: '70%'}}>

                        <CssTextField sx={{input: {color: 'white'}, style: {color: 'white'}}}
                                      id="select" select
                                      value={type}
                                      label='Tipo'
                                      onChange={handleChange}
                        >
                            {
                                types.map((type) => {
                                    return <MenuItem value={type}>{type}</MenuItem>
                                })
                            }
                        </CssTextField>
                    </FormControl>
                </Grid>
                {
                    selectedUsers.map((user) => {
                        return <Chip value={user}>{user}</Chip>
                    })
                }
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <FormControl sx={{width: '70%'}}>
                        <CssTextField sx={{input: {color: 'white'}, style: {color: 'white'}}}
                                      id="select" select
                                      label='Organizzatori'
                                      onChange={handleChange}
                        >
                            {
                                usersList.map((user) => {
                                    return <MenuItem value={user}>{user}</MenuItem>
                                })
                            }
                        </CssTextField>
                    </FormControl>
                </Grid>


            </Grid>
        </Grid>
    )
}

export default InitiativeCreateForm
