import React, {useState} from 'react';
import '../App.css';
import {Button, Grid, Typography} from "@mui/material";
import basquiaPulito from "../imgs/logo_CivicLife.png"
import ManageAccountsIcon from '@mui/icons-material/ManageAccounts';
import {BrowserRouter as Router, Route, Routes} from "react-router-dom";
import MainPage from "../MainPage/MainPage";
//Routing system
const Login = () => {
    const [auth, setAuth] = useState(false);
    const [admin, setAdmin]= useState(false)

    const loginWithFacebook = () => {
        console.log('login con FACEBOOK')
        setAuth(true)
    }

    const loginWithGoogle = () => {
        console.log('login con google')
        setAuth(true)
    }

    const loginWithSpid = () => {
        console.log('login con spid')
        setAuth(true)
    }
    const adminLogin = () => {
        console.log('login admin')
        setAuth(true)
        setAdmin(true)
    }

    return (
        <>
            <Grid container direction="row">
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <img src={basquiaPulito} width="320px" height="320px" alt={'civicLife Logo'}/>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <Typography style={{color: 'white', textAlign: 'center', fontSize: '4rem'}}>LOGIN</Typography>
                </Grid>
            </Grid>
            <Grid container direction="row">
                <Grid item xs={4} display="flex" justifyContent="center" alignItems="center" style={{marginTop: '5px'}}>
                    <Button
                        onClick={() => {
                            loginWithFacebook();
                        }}
                        style={{
                            borderRadius: 35,
                            backgroundColor: "#21b6ae",
                            padding: "18px 36px",
                            fontSize: "25px"
                        }}
                        variant="contained">FACEBOOK</Button>
                </Grid>
                <Grid item xs={4} display="flex" justifyContent="center" alignItems="center">
                    <Button
                        onClick={() => {
                            loginWithSpid();
                        }}
                        style={{
                            borderRadius: 35,
                            backgroundColor: "#e7b928",
                            padding: "18px 36px",
                            fontSize: "25px"
                        }}
                        variant="contained">SPID</Button>
                </Grid>
                <Grid item xs={4} display="flex" justifyContent="center" alignItems="center">
                    <Button
                        onClick={() => {
                            loginWithGoogle();
                        }}
                        style={{
                            borderRadius: 35,
                            backgroundColor: "#ff0000",
                            padding: "18px 36px",
                            fontSize: "25px"
                        }}
                        variant="contained">GOOGLE</Button>
                </Grid>
            </Grid>
            <Grid item xs={12} alignItems={'center'} display="flex">
                <Typography
                    style={{color: '#feac0d', fontSize: '1.2rem', marginLeft: '15px', bottom: 0}}>CivicLife</Typography>

                <Button
                    onClick={() => {
                        adminLogin()
                    }}
                    startIcon={<ManageAccountsIcon fontSize='large'/>} style={{
                    borderRadius: 35,
                    backgroundColor: "#808080",
                    padding: "18px 36px",
                    fontSize: "15px",
                    margin: 'auto',
                    position: 'fixed',
                    right: 20
                }}
                    variant="contained">ADMIN</Button>
            </Grid>
            {
                auth ?
                    <Router>
                        <Routes>
                            <Route exact path="/home" element={<MainPage isAdmin={admin}/>} />
                        </Routes>
                    </Router>: null
            }
        </>
    );
}

export default Login;
