import React, {useEffect, useState} from "react";
import {Button, Grid, ListItemSecondaryAction, ListItemText, Typography} from "@mui/material";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import {getAllUsers, StatusType, User} from "../APIs/UsersAPI";
import {useLocation} from "react-router-dom";
import AdminPanelSettingsIcon from '@mui/icons-material/AdminPanelSettings';

const UsersAdmin = () => {
    const location = useLocation();
    const tokenData = location.state.token;
    const [usersList, setUsersList] = useState<User[]>([])
    const [selectedUser, setSelectedUser] = useState<User | null>(null)
    const [firstLoad, setFirstLoad] = useState(true)
    const getUsers = async () => {
        console.log('tokenData', tokenData)
        const usersList = await getAllUsers(tokenData)
        setUsersList(usersList)
    }

    useEffect(() => {
        if (firstLoad) {
            getUsers()
        }
        setFirstLoad(false)
    })

    const handleUserClick = (user: any) => {
        setSelectedUser(user)
    }

    const handleOperation = () => {
        console.log('MODIFICO I DATI')
    }

    const statusTyphografy = (status: StatusType) => {
        if (status === StatusType.ACTIVE) {
            return <Typography sx={{color: 'green'}}>Attivo</Typography>
        } else if (status === StatusType.SUSPENDED) {
            return <Typography sx={{color: 'orange'}}>Sospeso</Typography>
        } else if (status === StatusType.BANNED) {
            return <Typography sx={{color: 'red'}}>Bannato</Typography>
        }
    }

    return (
        <Grid container direction="row">
            <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                <Typography
                    style={{color: '#feac0d', textAlign: 'center', fontSize: '3rem', position: "fixed", top: 10}}>Moderazione
                    utenti</Typography>
            </Grid>
            <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                <List sx={{
                    width: '80%',
                    overflow: 'auto',
                    maxHeight: 580,
                    position: 'fixed',
                    top: 150,
                    bottom: 100
                }}>
                    {usersList.map((value, index) => {
                        return (
                            <ListItem key={index} onClick={() => handleUserClick(value)}>
                                {
                                    value.admin ? <AdminPanelSettingsIcon sx={{color: '#feac0d'}}/> :
                                        <AccountCircleIcon fontSize='large'
                                                           sx={{color: selectedUser === value ? 'green' : 'white'}}/>
                                }
                                <ListItemButton sx={{color: selectedUser === value ? 'green' : 'white'}}>
                                    <ListItemText primary={<Typography>{value.name}</Typography>}
                                                  secondary={<Typography>{value.email}</Typography>}/>
                                </ListItemButton>
                                <ListItemSecondaryAction>{statusTyphografy(value.status)}</ListItemSecondaryAction>
                            </ListItem>
                        );
                    })}
                </List>
            </Grid>

            <Grid item xs={6} display="flex" justifyContent='center' alignItems="right">
                <Button style={{
                    position: 'fixed',
                    bottom: 20,
                    borderRadius: 35,
                    backgroundColor: "red",
                    padding: "10px 20px",
                    fontSize: "18px"
                }}
                        variant="contained"
                        onClick={() => {
                            handleOperation()
                        }}>
                    Banna
                </Button>
            </Grid>
            <Grid item xs={6} display="flex" justifyContent='center' alignItems="right">
                <Button style={{
                    position: 'fixed',
                    bottom: 20,
                    borderRadius: 35,
                    backgroundColor: "#feac0d",
                    padding: "10px 20px",
                    fontSize: "18px"
                }}
                        variant="contained"
                        onClick={() => {
                            handleOperation()
                        }}>
                    Silenzia
                </Button>
            </Grid>
        </Grid>
    );
}

export default UsersAdmin;
