import React, {useEffect, useState} from "react";
import {Button, Grid, ListItemSecondaryAction, ListItemText, Typography} from "@mui/material";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import {getAllUsers} from "../APIs/UsersAPI";
import {useLocation} from "react-router-dom";
import CircleIcon from '@mui/icons-material/Circle';

const UsersAdmin = () => {
    const location = useLocation();
    const tokenData = location.state.token;
    const [usersList, setUsersList] = useState<any[]>(['utente1', 'utente2', 'utente3', 'utente4', 'utente5', 'utente6', 'utente7', 'utente8', 'utente9', 'utente10', 'utente11', 'utente12'])
    const [selectedUser, setSelectedUser] = useState<any>(null)

    const getUsers = async () => {
        console.log('tokenData', tokenData)
        const usersList = await getAllUsers(tokenData)
        setUsersList(usersList)
    }

    useEffect(() => {
        getUsers()
    })

    const handleUserClick = (user: any) => {
        setSelectedUser(user)
    }

    const handleOperation = () => {
        console.log('MODIFICO I DATI')
    }

    const statusTyphografy = (status: string) => {
        if(status === 'ACTIVE'){

            return  <Typography sx={{color: '#00ff00', alignItems: 'center'}}><CircleIcon sx={{color: 'green'}}/> Attivo</Typography>
        }
        else if(status === 'SUSPENDED'){
            return <Typography sx={{color: '#ff0000'}}>Sospeso</Typography>
        }
        else{
            return <Typography sx={{color: '#ffff00'}}>Bannato</Typography>
        }
    }

    return (
        <Grid container direction="row">
            <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                <Typography style={{color: '#feac0d', textAlign: 'center', fontSize: '3rem', position: "fixed", top:10}}>Moderazione utenti</Typography>
            </Grid>
            <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                <List sx={{
                    width: '80%',
                    overflow: 'auto',
                    maxHeight: 580,
                    position:'fixed',
                    top: 150,
                    bottom: 100
                }}>
                    {usersList.map((value, index) => {
                        return (
                            <ListItem key={index} onClick={()=>handleUserClick(value)}>
                                <AccountCircleIcon fontSize='large' sx={{color: selectedUser === value ? 'green' : 'white'}}/>
                                <ListItemButton sx={{color: selectedUser === value ? 'green' : 'white'}}>
                                    <ListItemText primary={<Typography>{value.name}</Typography>} secondary={<Typography>{value.email}</Typography>}/>
                                </ListItemButton>
                                <ListItemSecondaryAction>{statusTyphografy(value.status)}</ListItemSecondaryAction>
                            </ListItem>
                        );
                    })}
                </List>
            </Grid>

            <Grid item xs={6} display="flex" justifyContent='center' alignItems="right">
                <Button style={{
                    position:'fixed',
                    bottom: 20,
                    borderRadius: 35,
                    backgroundColor: "red",
                    padding: "10px 20px",
                    fontSize: "18px"
                }}
                        variant="contained"
                        onClick={() => {
                            handleOperation()
                        }}>
                    Cancella
                </Button>
            </Grid>
            <Grid item xs={6} display="flex" justifyContent='center' alignItems="right">
                <Button style={{
                    position:'fixed',
                    bottom: 20,
                    borderRadius: 35,
                    backgroundColor: "#feac0d",
                    padding: "10px 20px",
                    fontSize: "18px"
                }}
                        variant="contained"
                        onClick={() => {
                            handleOperation()
                        }}>
                    Silenzia
                </Button>
            </Grid>
        </Grid>
    );
}

    export default UsersAdmin;
