import {AppBar, Button, Divider, Grid, IconButton, ListItem, ListItemButton, Typography} from "@mui/material";
import Box from "@mui/material/Box";

import React, {useState} from "react";
import '../App.css'
import {useLocation, useNavigate} from "react-router-dom";
import Toolbar from "@mui/material/Toolbar";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import {logoutUser} from "../APIs/OauthAPI";
import {CssTextField} from "../Utils/CustomTextFields";
import List from "@mui/material/List";


const VotationDetails = () => {
    const [partyList, setPatyList] = useState(['partito1', 'partito2', 'partito3', 'partito4', 'partito1', 'partito2', 'partito3', 'partito4', 'partito1', 'partito2', 'partito3', 'partito4', 'partito1', 'partito2', 'partito3', 'partito4'])
    const location = useLocation()
    const tokenData = location.state.token
    const isVisitor = location.state.isVisitor
    const navigate = useNavigate()
    const votation = location.state?.votation
    const isAdmin = location.state?.isAdmin

    const handlePartyDetailsOpen = (value: any) => {
        navigate('/votations/votationDetails/partyDetails', {
            state: {
                token: location.state?.token,
                email: location.state?.email,
                isAdmin: isAdmin,
                votation: location.state?.votation,
                party: value
            }
        })
    }

    const logout = async () => {
        if (tokenData !== null) {
            const response = await logoutUser(tokenData)
            if (typeof response === 'boolean') {
                if (response) {
                    navigate('/')
                } else {
                    console.log('error')
                }
            } else {
                navigate('/error', {state: {error: response}})
            }
        }
    }

    const goBack = () => {
        navigate(-1)
    }

    const login = () => {
        window.location.assign('http://localhost:8080/login')
    }

    return (
        <>
            <Grid item xs={12} justifyContent="center" alignItems="center">
                <Box sx={{flexGrow: 1}}>
                    <AppBar position="fixed" sx={{backgroundColor: '#3d4347'}}>
                        <Toolbar>
                            <IconButton
                                size="small"
                                edge="start"
                                aria-label="menu"
                                sx={{mr: 2}}
                            >
                                <ArrowBackIcon onClick={goBack} sx={{fontSize: '3rem', color: 'white'}}/>
                            </IconButton>

                            <Typography variant="h6" component="div" sx={{flexGrow: 1}}
                                        style={{
                                            justifyContent: 'center',
                                            color: '#feac0d',
                                            textAlign: 'center',
                                            fontSize: '1.8rem',
                                        }}>
                                {votation}
                            </Typography>
                            <Button
                                onClick={isVisitor ? login : logout}
                                style={{
                                    color: 'white',
                                    backgroundColor: isVisitor ? "green" : "red",
                                }}>

                                {isVisitor ? "login" : "logout"}
                            </Button>
                        </Toolbar>
                    </AppBar>
                </Box>
            </Grid>

            <Grid container display="flex" justifyContent="flex-start" alignItems="center"
                  sx={{width: '100%', marginTop: '65px'}} spacing={3}>
                <Grid item xs={12}>
                    <Box sx={{width: '100%', height: '100%', border: '2.5px solid #feac0d',}}>
                        <CssTextField
                            sx={{backgroundColor: 'white', width: '100%', height: '100%',}}
                            value={'ciao'}
                            maxRows={8}
                            multiline
                            InputProps={{
                                readOnly: true,
                            }}/>
                    </Box>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <List sx={{
                        width: '80%',
                        overflow: 'auto',
                        maxHeight: 580,
                    }}>
                        {partyList.map((value, index) => {
                            return (
                                <><
                                    ListItem key={index}>
                                    <ListItemButton onClick={() => {
                                        handlePartyDetailsOpen(value)
                                    }}>{value}</ListItemButton>
                                </ListItem>
                                    <Divider color='white'/>
                                </>
                            );
                        })}
                    </List>
                </Grid>
            </Grid>

        </>
    );

}

export default VotationDetails;
