import {AppBar, Button, Chip, Container, Grid, IconButton, Typography,} from "@mui/material";
import '../App.css'
import React, {useEffect, useState} from "react";
import Box from '@mui/material/Box';
import {CssTextField} from "../Utils/CustomTextFields";
import {useLocation, useNavigate} from "react-router-dom";
import Toolbar from "@mui/material/Toolbar";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import {isInstanceOfAuthError, logoutUser} from "../APIs/OauthAPI";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import FormControl from "@mui/material/FormControl";
import MenuItem from "@mui/material/MenuItem";
import {getAllUsersEmail} from "../APIs/UsersAPI";
import dayjs from "dayjs";


const InitiativeDetails = () => {
    const location = useLocation()
    const initiative = location.state.initiative
    const tokenData = location.state.token
    const user = location.state.user
    const isVisitor = location.state.isVisitor
    const navigate = useNavigate()
    const [value, setValue] = useState(initiative.description)
    const [usersList, setUsersList] = useState<string[]| null>(null)
    const [selectedUsers, setSelectedUsers] = useState<string[]>(initiative.idOrganizers)

    const userIsOrganizer = () => {
        if (user) {
            return (initiative.idOrganizers.includes(user.email));
        }
        return false;
    }

    console.log(userIsOrganizer())

    const getUsers = async () => {
        const response = await getAllUsersEmail(tokenData)
        if (isInstanceOfAuthError(response)) {
            navigate('/error', {state: {error: response}})
        } else {
            setUsersList(response.filter((email: string) => email !== tokenData.email))
        }
    }

    useEffect(() => {
        if(usersList === null && !isVisitor && user.email===initiative.idCreator){
            getUsers()
        }
    },[])

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setValue(event.target.value)
    }

    const logout = async () => {
        if (tokenData !== null) {
            const response = await logoutUser(tokenData)
            if (typeof response === 'boolean') {
                if (response) {
                    navigate('/')
                } else {
                    console.log('error')
                }
            } else {
                navigate('/error', {state: {error: response}})
            }
        }
    }

    const goBack = () => {
        navigate(-1)
    }

    const handleUserAdd = (user: any) => {
        if (!selectedUsers.includes(user)) {
            setSelectedUsers([...selectedUsers, user])
        }
    }

    const handleDelete = (userToDelete: any) => () => {
        console.log('chiamo questa funzione')
        setSelectedUsers((users: any[]) => users.filter((user) => user !== userToDelete));
    };

    return (
        <>
            <Grid item xs={12} justifyContent="center" alignItems="center">
                <Box sx={{flexGrow: 1}}>
                    <AppBar position="fixed" sx={{backgroundColor: '#3d4347'}}>
                        <Toolbar>
                            <IconButton
                                size="small"
                                edge="start"
                                aria-label="menu"
                                sx={{mr: 2}}
                            >
                                <ArrowBackIcon onClick={goBack} sx={{fontSize: '3rem', color: 'white'}}/>
                            </IconButton>
                            <Typography variant="h6" component="div" sx={{flexGrow: 1}}
                                        style={{
                                            justifyContent: 'center',
                                            color: 'white',
                                            textAlign: 'center',
                                            fontSize: '1.8rem',
                                        }}>Dettagli initiativa
                            </Typography>
                            <Button sx={{color: 'white', backgroundColor: 'red'}}
                                    onClick={logout}>
                                logout
                            </Button>
                        </Toolbar>
                    </AppBar>
                </Box>
            </Grid>
            <Grid container display="flex" justifyContent="center" xs={12} direction={'column'} sx={{
                width: '100%', position: 'fixed',
                top: 90
            }} spacing={3}>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <Typography variant="h6" component="div" sx={{flexGrow: 1}}
                                style={{
                                    justifyContent: 'center',
                                    color: 'white',
                                    textAlign: 'center',
                                    fontSize: '1.8rem',
                                }}>{initiative.name}
                    </Typography>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                        <Box sx={{width: '90%', height: '100%',border: '2.5px solid #feac0d',}}>
                            <CssTextField
                                sx={{backgroundColor: 'white', width: '100%', height: '100%',}}
                                value={value}
                                maxRows={5}
                                onChange={handleChange}
                                multiline
                                InputProps={{
                                    readOnly: !userIsOrganizer(),
                                }}/>
                        </Box>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <Typography  >Data Inizio: {dayjs.unix(initiative.startDate).format('DD/MM/YYYY')}</Typography>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <Typography >Data fine: {dayjs.unix(initiative.endDate).format('DD/MM/YYYY')}</Typography>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <Typography >Tipologia: </Typography>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    {initiative.type}
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <Typography >Creatore: </Typography>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <Chip sx={{backgroundColor: '#feac0d'}}
                                                icon={<AccountCircleIcon sx={{color: 'white'}}/>}
                                                label={initiative.idCreator}/>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <Typography>Organizzatori: </Typography>
                </Grid>
                <Grid item xs={12} justifyContent='center' alignItems="center">
                    {
                        selectedUsers.map((organizer: string) => {
                            return (
                                <Grid item display="flex" xs={12} justifyContent="center" alignItems="center">
                                    <Chip sx={{backgroundColor: '#feac0d', marginTop: '10px'}}
                                          icon={<AccountCircleIcon sx={{color: 'white'}}/>}
                                          label={organizer}
                                          onDelete={organizer===user.email|| user.email===initiative.idCreator? handleDelete(organizer): undefined}
                                    />
                                </Grid>)
                        })
                    }
                </Grid>
                {
                    user && user.email===initiative.idCreator && !isVisitor?
                        <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                            <FormControl sx={{width: '70%'}}>
                                <CssTextField sx={{input: {color: 'white'}, style: {color: 'white'}}}
                                              select
                                              label='Organizzatori'>
                                    {
                                        usersList ? usersList.map((user) => {
                                            return <MenuItem onClick={() => {
                                                handleUserAdd(user)
                                            }} value={user}>{user}</MenuItem>
                                        }): null
                                    }
                                </CssTextField>
                            </FormControl>
                        </Grid> : null
                }
                <Grid item xs={12} display="flex" justifyContent='center' alignItems="right">
                    {isVisitor ? null : userIsOrganizer() ?
                        <><Button style={{
                            borderRadius: 35,
                            backgroundColor: "#92d36e",
                            padding: "18px 36px",
                            fontSize: "15px",
                        }}
                                  variant="contained">
                            Modifica
                        </Button><Button style={{
                            borderRadius: 35,
                            backgroundColor: "red",
                            padding: "18px 36px",
                            fontSize: "15px",
                            marginLeft: '30px'
                        }}
                                         variant="contained">
                            Cancella
                        </Button></>
                        :
                        <Button style={{
                            borderRadius: 35,
                            backgroundColor: "#92d36e",
                            padding: "18px 36px",
                            fontSize: "15px",
                        }}
                                variant="contained">
                            Partecipa
                        </Button>
                    }

                </Grid>
            </Grid>



        </>

    );
}

export default InitiativeDetails;
