// TODO: create a form for initiative creation
import {Button, Divider, Grid, MenuItem, TextField, Typography} from "@mui/material";
import UpperButtonMenu from "../Utils/UpperButtonMenu";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import React, {useState} from "react";
import {CssTextField} from "../Utils/CustomTextFields";
import Chip from '@mui/material/Chip';
import Person2Icon from '@mui/icons-material/Person2';


const CreateInitiativesForm = ()=>{
    const types=['Beneficenza', 'Sociale', 'Privato', 'Urbanistico', 'Svago']
    const [allUsers, setAllUsers]= useState(['utente1', 'utente2', 'utente3', 'utente4', 'utente5', 'utente6', 'utente7', 'utente8', 'utente9', 'utente10', 'utente11'])
    const [organizers, setOrganizers]= useState<any>([])

    const addOrganizer=(user:any)=>{
        const new_organizers = organizers.push(user)
        setOrganizers(new_organizers)
    }
    const handleDelete=(user:any)=>{
        const new_organizers = organizers.filter((old_user: any)=> old_user!==user)
        setOrganizers(new_organizers)
    }
    const handleCreateInitiative = () => {
        console.log('CREA')
    }

// private String name;
// private String description;
// private String status;
// private String type;
// private String idCreator;
// private List<String> idOrganizers;
// private List<String> idMembers;
// private long startDate;
// private long endDate;
// private String location;
    return(
        <>
            <Grid container direction="column" spacing={2}>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center" sx={{
                    width: '100%',
                    position: 'fixed',
                    margin: "auto",
                    top: 65,
                    right: 0
                }}>
                    <Typography
                        style={{color: '#feac0d', textAlign: 'center', fontSize: '3rem'}}>Crea la tua iniziativa</Typography>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <CssTextField sx={{width:'60%', input: {color: 'white'}, style: {color: 'white'}}} id="standard-basic" label="Nome" variant="standard" />
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <CssTextField sx={{width:'60%', input: {color: 'white'}, style: {color: 'white'}}} id="standard-basic" label="Descrizione" variant="standard" />
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <CssTextField
                        sx={{width:'60%', color: 'white', input:{color: 'white'}, style: {color: 'white'}}}
                        id="outlined-select-currency"
                        select
                        label="Tipologia"
                        defaultValue={types[0]}
                    >
                        {types.map((value, index) => (
                            <MenuItem key={value} value={value}>
                                {value}
                            </MenuItem>
                        ))}
                    </CssTextField>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    {organizers.map((value: string)=>{
                        console.log(value)
                        return (
                            <Chip
                                avatar={<Person2Icon/>}
                                label={value}
                                onDelete={()=>{handleDelete(value)}}
                            />
                        )
                    })
                    }
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <CssTextField
                        sx={{width:'60%', color: 'white', input:{color: 'white'}, style: {color: 'white'}}}
                        id="outlined-select-currency"
                        select
                        label="Aggiungi organizzatore"
                    >
                        {allUsers.map((value, index) => (
                            <MenuItem key={value} value={value} onClick={()=>{addOrganizer(value)}}>
                                {value}
                            </MenuItem>
                        ))}
                    </CssTextField>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <Button style={{
                        marginTop: '15px',
                        borderRadius: 35,
                        backgroundColor: "#92d36e",
                        padding: "10px 20px",
                        fontSize: "18px"
                    }}
                            variant="contained"
                            onClick={()=>{handleCreateInitiative()}}>
                        CREA
                    </Button>
                </Grid>
            </Grid>
        </>
    )
}

export default CreateInitiativesForm
