import {AppBar, Button, Grid, IconButton, Typography,} from "@mui/material";
import '../App.css'
import React, {useState} from "react";
import Box from '@mui/material/Box';
import {CssTextField} from "../Utils/CustomTextFields";
import {useLocation, useNavigate} from "react-router-dom";
import Toolbar from "@mui/material/Toolbar";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import {logoutUser} from "../APIs/OauthAPI";


const InitiativeDetails = () => {
    const location = useLocation()
    const initiative = location.state.initiative
    const tokenData = location.state.token
    const user = location.state.user
    const isVisitor = location.state.isVisitor
    const navigate = useNavigate()
    const [value, setValue] = useState(initiative.description)

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setValue(event.target.value)
    }

    const logout = async () => {
        if (tokenData !== null) {
            const response = await logoutUser(tokenData)
            if (typeof response === 'boolean') {
                if (response) {
                    navigate('/')
                } else {
                    console.log('error')
                }
            } else {
                navigate('/error', {state: {error: response}})
            }
        }
    }

    const goBack = () => {
        navigate(-1)
    }

    return (
        <>
            <Grid item xs={12} justifyContent="center" alignItems="center">
                <Box sx={{flexGrow: 1}}>
                    <AppBar position="fixed" sx={{backgroundColor: '#3d4347'}}>
                        <Toolbar>
                            <IconButton
                                size="small"
                                edge="start"
                                aria-label="menu"
                                sx={{mr: 2}}
                            >
                                <ArrowBackIcon onClick={goBack} sx={{fontSize: '3rem', color: 'white'}}/>
                            </IconButton>
                            <Typography variant="h6" component="div" sx={{flexGrow: 1}}
                                        style={{
                                            justifyContent: 'center',
                                            color: 'white',
                                            textAlign: 'center',
                                            fontSize: '1.8rem',
                                        }}>{initiative.name}
                            </Typography>
                            <Button sx={{color: 'white', backgroundColor: 'red'}}
                                    onClick={logout}>
                                logout
                            </Button>
                        </Toolbar>
                    </AppBar>
                </Box>
            </Grid>

            <Grid item xs={12} display="flex" justifyContent="center" alignItems="center" sx={{
                width: '100%', position: 'fixed',
                margin: "auto",
                top: 200, right: 0
            }}>
                <Box sx={{width: '70%', height: '100%', border: '2.5px solid #feac0d',}}>
                    <CssTextField
                        sx={{backgroundColor: 'white'}}
                        value={value}
                        maxRows={15}
                        onChange={handleChange}
                        multiline
                        InputProps={{
                            readOnly: true,
                        }}/>
                </Box>

            </Grid><Grid item xs={12} display="flex" justifyContent='center' alignItems="right">
            <Button style={{
                borderRadius: 35,
                backgroundColor: "#92d36e",
                padding: "18px 36px",
                fontSize: "15px",
                position: 'fixed',
                margin: "auto",
                bottom: 180,
            }}
                    variant="contained">
                Partecipa
            </Button>
        </Grid></>

    );
}

export default InitiativeDetails;
