import {AppBar, Button, Chip, Fab, Grid, IconButton, Typography, useScrollTrigger,} from "@mui/material";
import '../App.css'
import React, {useState} from "react";
import Box from '@mui/material/Box';
import {CssTextField} from "../Utils/CustomTextFields";
import {useLocation, useNavigate} from "react-router-dom";
import Toolbar from "@mui/material/Toolbar";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import {logoutUser} from "../APIs/OauthAPI";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import {Container} from "@mui/material";
import ScrollToTop from "react-scroll-to-top";
import KeyboardArrowUpIcon from "@mui/icons-material/KeyboardArrowUp";
import Fade from "@mui/material/Fade";


const InitiativeDetails = () => {
    const location = useLocation()
    const initiative = location.state.initiative
    const tokenData = location.state.token
    const user = location.state.user
    const isVisitor = location.state.isVisitor
    const navigate = useNavigate()
    const [value, setValue] = useState(initiative.description)

    interface Props {
        /**
         * Injected by the documentation to work in an iframe.
         * You won't need it on your project.
         */
        window?: () => Window;
        children: React.ReactElement;
    }

    function ScrollTop(props: Props) {
        const { children, window } = props;
        // Note that you normally won't need to set the window ref as useScrollTrigger
        // will default to window.
        // This is only being set here because the demo is in an iframe.
        const trigger = useScrollTrigger({
            target: window ? window() : undefined,
            disableHysteresis: true,
            threshold: 100,
        });

        const handleClick = (event: React.MouseEvent<HTMLDivElement>) => {
            const anchor = (
                (event.target as HTMLDivElement).ownerDocument || document
            ).querySelector('#back-to-top-anchor');

            if (anchor) {
                anchor.scrollIntoView({
                    block: 'center',
                });
            }
        };

        return (
            <Fade in={trigger}>
                <Box
                    onClick={handleClick}
                    role="presentation"
                    sx={{ position: 'fixed', bottom: 16, right: 16 }}
                >
                    {children}
                </Box>
            </Fade>
        );
    }

    const userIsOrganizer = () => {
        if (user) {
            return !!(initiative.idCreator === user.email || initiative.idOrganizers.includes(user.email));
        }
        return false;
    }

    console.log('sono un organizzatore?', userIsOrganizer())

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setValue(event.target.value)
    }

    const logout = async () => {
        if (tokenData !== null) {
            const response = await logoutUser(tokenData)
            if (typeof response === 'boolean') {
                if (response) {
                    navigate('/')
                } else {
                    console.log('error')
                }
            } else {
                navigate('/error', {state: {error: response}})
            }
        }
    }

    const goBack = () => {
        navigate(-1)
    }

    return (
        <>
            <Grid item xs={12} justifyContent="center" alignItems="center">
                <Box sx={{flexGrow: 1}}>
                    <AppBar position="fixed" sx={{backgroundColor: '#3d4347'}}>
                        <Toolbar>
                            <IconButton
                                size="small"
                                edge="start"
                                aria-label="menu"
                                sx={{mr: 2}}
                            >
                                <ArrowBackIcon onClick={goBack} sx={{fontSize: '3rem', color: 'white'}}/>
                            </IconButton>
                            <Typography variant="h6" component="div" sx={{flexGrow: 1}}
                                        style={{
                                            justifyContent: 'center',
                                            color: 'white',
                                            textAlign: 'center',
                                            fontSize: '1.8rem',
                                        }}>{initiative.name}
                            </Typography>
                            <Button sx={{color: 'white', backgroundColor: 'red'}}
                                    onClick={logout}>
                                logout
                            </Button>
                        </Toolbar>
                    </AppBar>
                </Box>
            </Grid>

            <Grid item xs={12} display="flex" justifyContent="center" alignItems="center" sx={{
                width: '100%', position: 'fixed',
                margin: "auto",
                top: 120, right: 0
            }}>
                <Container>
                    <Box sx={{width: '100%', height: '70%', border: '2.5px solid #feac0d',}}>
                        <CssTextField
                            sx={{backgroundColor: 'white'}}
                            value={value}
                            maxRows={5}
                            onChange={handleChange}
                            multiline
                            InputProps={{
                                readOnly: !userIsOrganizer(),
                            }}/>
                    </Box>
                    <ScrollTop>
                        <Fab size="small" aria-label="scroll back to top">
                            <KeyboardArrowUpIcon />
                        </Fab>
                    </ScrollTop>
                </Container>

            </Grid>
            <Grid container direction={"column"} justifyContent="center" alignItems="center" sx={{position:'fixed', margin:'auto', top: 300}}>
                <Grid item xs={12} justifyContent="center" alignItems="center">
                    <Typography variant="h6" component="div">Creatore: {initiative.idCreator}</Typography>
                </Grid>
                <Grid item xs={12} justifyContent='center' alignItems="center">
                <Typography variant="h6" component="div" sx={{flexGrow: 1}}>Organizzatori: </Typography>
                {
                    initiative.idOrganizers.map((organizer: string) => {

                        return (
                            <Grid item xs={12} justifyContent="center" alignItems="center">
                                <Chip sx={{backgroundColor: '#feac0d', marginTop: '10px'}}
                                      icon={<AccountCircleIcon sx={{color: 'white'}}/>}
                                      label={organizer}
                                />
                            </Grid>)
                    })
                }
            </Grid>
            </Grid>
            <Grid container direction={"column"} justifyContent="center" alignItems="center">

            </Grid>


            <Grid item xs={12} display="flex" justifyContent='center' alignItems="right">
                {isVisitor ? null : userIsOrganizer() ?
                    <Button style={{
                        borderRadius: 35,
                        backgroundColor: "#92d36e",
                        padding: "18px 36px",
                        fontSize: "15px",
                        position: 'fixed',
                        margin: "auto",
                        bottom: 180,
                    }}
                            variant="contained">
                        Modifica
                    </Button>
                    :
                    <Button style={{
                        borderRadius: 35,
                        backgroundColor: "#92d36e",
                        padding: "18px 36px",
                        fontSize: "15px",
                        position: 'fixed',
                        margin: "auto",
                        bottom: 180,
                    }}
                            variant="contained">
                        Partecipa
                    </Button>
                }

            </Grid>
        </>

    );
}

export default InitiativeDetails;
