import {Navigate, useLocation, useNavigate} from "react-router-dom";
import React, {useState} from "react";
import {Button, Divider, Grid} from "@mui/material";
import UpperButtonMenu from "../Utils/UpperButtonMenu";
import Box from "@mui/material/Box";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";

const InitiativesAdministration = () => {
    const navigate = useNavigate();
    const [allInitiativesList, setAllInitiativesList] = useState<any[]>(['Iniziativa1', 'Iniziativa2', 'Iniziativa3', 'Iniziativa4', 'Iniziativa5', 'Iniziativa6', 'Iniziativa7'])

    const location = useLocation()
    const isAdmin = location.state?.isAdmin
    const token = location.state?.token

    if (isAdmin === undefined || token === undefined || !isAdmin) {
        console.log('non sei loggato come admin')
        return <Navigate to='/'/>
    }

    const handleInitiativeDetailsOpen = (value: any) => {
        navigate('/initiatives/' + value, {state: {token: token, isAdmin: isAdmin}})
    }

    return (
        <>
            <Grid container spacing={2} justifyContent="center" alignItems="center">
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <Box sx={{width: '70%', height: '100%'}}>
                        {allInitiativesList.map((value, index) => {
                            return (
                                <>
                                    <ListItem key={index}>
                                        <ListItemButton
                                            onClick={() => handleInitiativeDetailsOpen(value)}>{value}</ListItemButton>
                                    </ListItem>
                                    <Divider color='white'/>
                                </>
                            );
                        })}


                    </Box>
                </Grid>
                <Grid item xs={4} display="flex" justifyContent="center">
                    <Button style={{
                        borderRadius: 35,
                        backgroundColor: "red",
                        padding: "10px 20px",
                        fontSize: "18px"
                    }}
                            variant="contained">
                        {'Elimina iniziativa'}
                    </Button>
                </Grid>
                <Grid item xs={4} display="flex" justifyContent="center">
                    <Button style={{
                        borderRadius: 35,
                        backgroundColor: '#feac0d',
                        padding: "10px 20px",
                        fontSize: "18px"
                    }}
                            variant="contained">
                        {'altra cosa'}
                    </Button>
                </Grid>
                <Grid item xs={4} display="flex" justifyContent="center">
                    <Button style={{
                        borderRadius: 35,
                        backgroundColor: '#feac0d',
                        padding: "10px 20px",
                        fontSize: "18px"
                    }}
                            variant="contained">
                        {'altra cosa'}
                    </Button>
                </Grid>
            </Grid>
        </>

    );
}

export default InitiativesAdministration
