import {TokenData} from "./OauthAPI";
import axios from "axios";
import {Base64} from "js-base64";

export enum StatusType{
    ACTIVE,SUSPENDED,BANNED
}

export interface User {
    email: string,
    name: string,
    surname: string,
    admin: boolean,
    fiscalCode:string,
    residence:string,
    birthDate:number,
    domicile:string,
    status:StatusType,
    telephonNumber: number
}

export const getLoggedUser = (tokenData: TokenData) => {
    console.log('recupero utente', tokenData)
    const emailBase64= Base64.encode(tokenData.email)
    const providerBase64= Base64.encode(tokenData.provider)
    const tokenBase64= Base64.encode(tokenData.token)
    const url = 'http://localhost:8080/userAPI/v1/user/get/' + tokenData.email+ '?email='+ emailBase64 + '&provider=' + providerBase64 + '&token=' + tokenBase64
    return axios.get<User>(url).then((response) => {
        console.log('response', response.data)
        return response.data
    })
}

export const getAllUsers = (tokenData: TokenData) => {
    const emailBase64= Base64.encode(tokenData.email)
    const providerBase64= Base64.encode(tokenData.provider)
    const tokenBase64= Base64.encode(tokenData.token)
    const url = 'http://localhost:8080/userAPI/v1/users/?email='+ emailBase64 + '&provider=' + providerBase64 + '&token=' + tokenBase64
    return axios.get<User[]>(url).then((response) => {
        console.log('response', response.data)
        return response.data
    })
}


const configGeneratorEncoder=(base_url: string, tokenData:TokenData, value:string)=>{
    value = Base64.encode(value)
    const emailBase64= Base64.encode(tokenData.email)
    const providerBase64= Base64.encode(tokenData.provider)
    const tokenBase64= Base64.encode(tokenData.token)
    const url= base_url+tokenData.email+'?email='+ emailBase64 + '&provider=' + providerBase64 + '&token=' + tokenBase64
    console.log('url',url)
    return  {
        method:'post',
        url:url,
        data: value
    }
}


export const updateUser=(tokenData: TokenData, newUser: User)=>{
    console.log('newUser',newUser)
    const emailBase64= Base64.encode(tokenData.email)
    const providerBase64= Base64.encode(tokenData.provider)
    const tokenBase64= Base64.encode(tokenData.token)

    const url= 'http://localhost:8080/userAPI/v1/user/update/' + tokenData.email+ '?email='+ emailBase64 + '&provider=' + providerBase64 + '&token=' + tokenBase64
    console.log('url',url);

    axios.post(url,
        JSON.stringify(newUser),
        {
            headers: {"Content-Type": "text/plain"}
        })
        .then(value => {
            console.log("SONO NEL THEN della post")
            console.log(value.data)
            return value.data;
        }).catch(function(error) {
            console.log(error)
            return false;
    });
}

export const updateSingleParameter=(tokenData: TokenData, value:string)=> {
    console.log('param',value)
    const emailBase64= Base64.encode(tokenData.email)
    const providerBase64= Base64.encode(tokenData.provider)
    const tokenBase64= Base64.encode(tokenData.token)

    const url= 'http://localhost:8080/userAPI/v1/user/update/residence/' + tokenData.email+ '?email='+ emailBase64 + '&provider=' + providerBase64 + '&token=' + tokenBase64
    console.log('url',url);

    const headers = {
        "Content-Type" : "text/plain"
    }

    let return_value

    fetch(url, {
        method : "POST",
        mode: 'no-cors',
        headers: headers,
        body: Base64.encode(value)
    }).then((response) =>{
            return_value = response.body
            console.log("HO IL BODY ", response.status)
        }
    ).catch((error) =>{
        return_value = error;
        console.log("C'E'ERRORE")
        console.log(error);
    });

    console.log('return_value', return_value)
    return return_value;
}
