                        <Chip sx={{color: 'green'}}
                              icon={<CheckCircleIcon sx={{color: 'green'}}/>}
                              label={'Successo'}
                              variant="outlined"
                        />
                    }
                </DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        {errors.length > 0 ?
                            errors.map((error: string) => {
