import {Grid, Typography} from "@mui/material";
import React from "react";
import {CssTextField} from "../Utils/CustomTextFields";
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select, { SelectChangeEvent } from '@mui/material/Select';

const InitiativeCreateForm = () => {
    const types = ['tipo1', 'tipo2', 'tipo3']
    const [type, setType] = React.useState(types[0]);

    const handleChange = (event: SelectChangeEvent) => {
        setType(event.target.value as string);
    };

    return (

        <Grid container sx={{position: 'fixed', bottom: 950}}>
            {/*    <Grid item xs={12} display="flex" sx={{*/}
            {/*    width: '100%',*/}
            {/*    margin: "auto",*/}
            {/*    top: 0,*/}
            {/*    right: 0*/}
            {/*}}>*/}
            {/*    <IconButton><KeyboardBackspaceIcon sx={{fontSize: 60, color: '#ffffff'}}/></IconButton>*/}
            {/*</Grid>*/}
            <Grid item xs={12} display="flex" justifyContent="center" alignItems="center" sx={{
                width: '100%',
                margin: "auto",
                position: 'fixed',
                top: 20
            }}>
                <Typography style={{color: '#feac0d', textAlign: 'center', fontSize: '3rem'}}>Crea la tua
                    iniziativa</Typography>
            </Grid>
            <Grid container direction='row' spacing={2} sx={{position: 'fixed', top: 150, bottom: 950}}>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <CssTextField sx={{input: {color: 'white'}, style: {color: 'white'}, width: '70%'}}
                                  label={'Nome'}/>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <CssTextField sx={{input: {color: 'white'}, style: {color: 'white'}, width: '70%'}}
                                  label={'Descrizione'}/>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <CssTextField sx={{input: {color: 'white'}, style: {color: 'white'}, width: '70%'}}
                                  label={'Luogo'}/>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center" sx={{input: {color: 'white'}, style: {color: 'white'},width:'70%'}}>
                    <FormControl>
                        <InputLabel id="demo-simple-select-label">Age</InputLabel>
                        <CssTextField

                            value={type}
                            label='Tipo'
                            onChange={handleChange}
                        >
                            {
                                types.map((type) => {
                                    return <MenuItem value={type}>{type}</MenuItem>
                                })
                            }
                        </CssTextField>
                    </FormControl>
                </Grid>


            </Grid>
        </Grid>
    )
}

export default InitiativeCreateForm
