// TODO: create a form for initiative creation
import {Button, Grid, MenuItem, Typography} from "@mui/material";
import React, {useState} from "react";
import {CssTextField} from "../Utils/CustomTextFields";
import Chip from '@mui/material/Chip';
import Person2Icon from '@mui/icons-material/Person2';
import {Navigate, useLocation} from "react-router-dom";


const CreateInitiativesForm = () => {
    const location = useLocation()
    const types = ['Beneficenza', 'Sociale', 'Privato', 'Urbanistico', 'Svago']
    const [allUsers, setAllUsers] = useState(['utente1', 'utente2', 'utente3', 'utente4', 'utente5', 'utente6', 'utente7', 'utente8', 'utente9', 'utente10', 'utente11'])
    const [name, setName]= useState('')
    const [description, setDescription]= useState('')
    const [selectedType, setSelectedType]= useState(types[0])
    const [place, setPlace]= useState('')
    const [startDate, setStartDate]= useState('')
    const [endDate, setEndDate]= useState('')
    const [organizers, setOrganizers] = useState<any>([])
    const loginType = location.state?.loginType
    const token = location.state?.token
    const isAdmin = location.state?.isAdmin

    if(loginType === undefined || token === undefined || isAdmin === undefined){
        console.log('non sei loggato')
        return <Navigate to='/'/>
    }



    const handleCreateInitiative = () => {
        if(name!=='' && description!=='' && place!=='' && startDate!=='' && endDate!==''){
            console.log('creo iniziativa')
        }else{
            console.log('manca qualcosa')
        }
    }

    const handleNameChange=(event: { target: { value: React.SetStateAction<string>; }; })=>{
        setName(event.target.value)
    }

    const handleDescriptionChange=(event: { target: { value: React.SetStateAction<string>; }; })=>{
        setDescription(event.target.value)
    }

    const handlePlaceChange=(event: { target: { value: React.SetStateAction<string>; }; })=>{
        setPlace(event.target.value)
    }

    const handleStartDateChange=(event: { target: { value: React.SetStateAction<string>; }; })=>{
        setStartDate(event.target.value)
    }

    const handleEndDateChange=(event: { target: { value: React.SetStateAction<string>; }; })=>{
        setEndDate(event.target.value)
    }

    const handletypeChange=(event: { target: { value: React.SetStateAction<string>; }; })=>{
        setSelectedType(event.target.value)
    }

    const addOrganizer = (user: any) => {
        setOrganizers([...organizers, user])
    }
    const handleDelete = (user: any) => {
        const new_organizers = organizers.filter((old_user: any) => old_user !== user)
        setOrganizers(new_organizers)
    }

    return (
        <>
            <Grid container direction="column" spacing={2}>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <Typography style={{color: 'white', textAlign: 'center', fontSize: '3rem'}}>Crea la tua iniziativa</Typography>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <CssTextField onChange={handleNameChange} sx={{width: '60%', input: {color: 'white'}, style: {color: 'white'}}} label="Nome"/>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <CssTextField onChange={handleDescriptionChange} sx={{width: '60%', input: {color: 'white'}, style: {color: 'white'}}}
                                   label="Descrizione"/>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <CssTextField
                        sx={{width: '60%', color: 'white', input: {color: 'white'}, style: {color: 'white'}}}
                        id="outlined-select-currency"
                        select
                        label="Tipologia"
                        defaultValue={types[0]}
                        onChange={handletypeChange}
                    >
                        {types.map((value, index) => (
                            <MenuItem key={value} value={value}>
                                {value}
                            </MenuItem>
                        ))}
                    </CssTextField>
                </Grid>

                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    {
                        organizers.map((value: string) => {
                            return (
                                <Chip
                                    avatar={<Person2Icon style={{color: '#feac0d'}}/>}
                                    sx={{color: 'white', marginLeft: '10px'}}
                                    label={value}
                                    onDelete={() => {
                                        handleDelete(value)
                                    }}
                                />
                            )
                        })
                    }
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <CssTextField
                        sx={{width: '60%', color: 'white', input: {color: 'white'}, style: {color: 'white'}}}
                        id="outlined-select-currency"
                        select
                        label="Aggiungi organizzatore"
                    >
                        {allUsers.map((value, index) => (
                            <MenuItem key={value} value={value} onClick={() => {
                                addOrganizer(value)
                            }}>
                                {value}
                            </MenuItem>
                        ))}
                    </CssTextField>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <CssTextField onChange={handlePlaceChange} sx={{width: '60%', input: {color: 'white'}, style: {color: 'white'}}}
                                  label="Luogo"/>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <CssTextField
                        id="date"
                        label="Data inizio"
                        type="date"
                        sx={{ width: '60%' }}
                        InputLabelProps={{
                            shrink: true,
                        }}
                        onChange={handleStartDateChange}
                    />
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <CssTextField
                        id="date"
                        label="Data fine"
                        type="date"
                        sx={{ width: '60%' }}
                        InputLabelProps={{
                            shrink: true,
                        }}
                        onChange={handleEndDateChange}
                    />
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <Button style={{
                        marginTop: '15px',
                        borderRadius: 35,
                        backgroundColor: "#92d36e",
                        padding: "10px 20px",
                        fontSize: "18px"
                    }}
                            variant="contained"
                            onClick={() => {
                                handleCreateInitiative()
                            }}>
                        CREA
                    </Button>
                </Grid>

            </Grid>
        </>
    )
}

export default CreateInitiativesForm
