// TODO: create a form for initiative creation
import {Button, Divider, Grid, MenuItem, TextField, Typography} from "@mui/material";
import UpperButtonMenu from "../Utils/UpperButtonMenu";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import React from "react";
import {CssTextField} from "../Utils/CustomTextFields";

const CreateInitiativesForm = ()=>{
    const types=['Beneficenza', 'Sociale', 'Privato', 'Urbanistico', 'Svago']
    const handleCreateInitiative = () => {
        console.log('CREA')
    }

// private String name;
// private String description;
// private String status;
// private String type;
// private String idCreator;
// private List<String> idOrganizers;
// private List<String> idMembers;
// private long startDate;
// private long endDate;
// private String location;
    return(
        <>
            <Grid container direction="column" spacing={2}>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center" sx={{
                    width: '100%',
                    position: 'fixed',
                    margin: "auto",
                    top: 65,
                    right: 0
                }}>
                    <Typography
                        style={{color: '#feac0d', textAlign: 'center', fontSize: '3rem'}}>Crea la tua iniziativa</Typography>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <CssTextField sx={{width:'60%', input: {color: 'white'}, style: {color: 'white'}}} id="standard-basic" label="Nome" variant="standard" />
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <CssTextField sx={{width:'60%', input: {color: 'white'}, style: {color: 'white'}}} id="standard-basic" label="Descrizione" variant="standard" />
                </Grid>
                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <CssTextField
                        sx={{width:'60%', input: {color: 'white'}, style: {color: 'white'}}}
                        id="outlined-select-currency"
                        label="Tipologia"
                        defaultValue={types[0]}
                    >
                        {types.map((value, index) => (
                            <MenuItem key={value} value={value}>
                                {value}
                            </MenuItem>
                        ))}
                    </CssTextField>
                </Grid>

                <Grid item xs={12} display="flex" justifyContent='center' alignItems='center'>
                    <Button style={{
                        marginTop: '15px',
                        borderRadius: 35,
                        backgroundColor: "#92d36e",
                        padding: "10px 20px",
                        fontSize: "18px"
                    }}
                            variant="contained"
                            onClick={()=>{handleCreateInitiative()}}>
                        CREA
                    </Button>
                </Grid>
            </Grid>
        </>
    )
}

export default CreateInitiativesForm
