import {AppBar, Button, Chip, Container, Grid, IconButton, styled, Typography,} from "@mui/material";
import '../App.css'
import React, {useEffect, useState} from "react";
import Box from '@mui/material/Box';
import {CssTextField} from "../Utils/CustomTextFields";
import {useLocation, useNavigate} from "react-router-dom";
import Toolbar from "@mui/material/Toolbar";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import {isInstanceOfAuthError, logoutUser} from "../APIs/OauthAPI";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import FormControl from "@mui/material/FormControl";
import MenuItem from "@mui/material/MenuItem";
import {getAllUsersEmail} from "../APIs/UsersAPI";
import dayjs, {Dayjs} from "dayjs";
import {Initiative, InitiativeType, InitiativeTypeColor} from "../APIs/InitiativeAPI";
import {AdapterDayjs} from "@mui/x-date-pickers/AdapterDayjs";
import {DatePicker, LocalizationProvider} from "@mui/x-date-pickers";


const InitiativeDetails = () => {
    const location = useLocation()
    const initiative = location.state.initiative
    const tokenData = location.state.token
    const user = location.state.user
    const isVisitor = location.state.isVisitor
    const navigate = useNavigate()
    const [description, setDescription] = useState(initiative.description)
    const [usersList, setUsersList] = useState<string[]| null>(null)
    const [selectedUsers, setSelectedUsers] = useState<string[]>(initiative.idOrganizers)
    const [startDate, setStartDate] = useState<Dayjs | null>(null)
    const [endDate, setEndDate] = useState<Dayjs | null>(null)
    const initiativeType: InitiativeType = initiative.type


    const handleStartDateChanged = (newValue: Dayjs|null) => {
        if (dayjs(newValue, "DD/MM/YYYY", true).isValid()) {
            console.log('sto settando',newValue)
            setStartDate(newValue);
        }
        else{
            console.log("Invalid date")
        }
    }

    const handleEndDateChanged = (newValue: Dayjs|null) => {
        if (dayjs(newValue, 'DD/MM/YYYY', true).isValid()) {
            setEndDate(newValue);
        }
        else{
            console.log("Invalid date")
        }
    }

    const userIsOrganizer = () => {
        if (user) {
            return (initiative.idOrganizers.includes(user.email));
        }
        return false;
    }

    const isReadOnly = () => {
        if(userIsOrganizer()){
            return false
        }
        else return user.email !== initiative.idCreator;
    }

    console.log(userIsOrganizer())

    const getUsers = async () => {
        const response = await getAllUsersEmail(tokenData)
        if (isInstanceOfAuthError(response)) {
            navigate('/error', {state: {error: response}})
        } else {
            setUsersList(response)
        }
    }

    useEffect(() => {
        if(usersList === null && !isVisitor && user.email===initiative.idCreator){
            getUsers()
        }
    },[])

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setDescription(event.target.value)
    }

    function toTimestamp(strDate: string){
        const datum = Date.parse(strDate);
        return datum/1000;
    }

    const dateCalculator = (date: Dayjs | null): number => {
        if(date){
            console.log(date)
            console.log(date.format("DD/MM/YYYY"))
            const timestamp = Date.parse(date.format("DD/MM/YY"))/1000
            console.log(timestamp)
            return timestamp
        }
        else{
            return initiative.startDate
        }

    }

    const handleModify = () => {
        if(!isReadOnly()){
            console.log('modify')
            if(initiative.description !== description ||
                ( startDate && initiative.startDate !== toTimestamp(startDate.format("DD/MM/YYYY"))) ||
                (endDate && initiative.endDate !== toTimestamp(endDate.format("DD/MM/YYYY")))){

                const newInitiative: Initiative = {
                    id: initiative.id,
                    idCreator: initiative.idCreator,
                    idOrganizers: initiative.idOrganizers,
                    description: description,
                    startDate: dateCalculator(startDate),
                    endDate: 5,
                    type: initiative.type,
                    idMembers: initiative.idMembers,
                    location: initiative.location,
                    name: initiative.name,
                }
                console.log(newInitiative)
            }
            if(initiative.idOrganizers !== selectedUsers){
                if(user.email === initiative.idCreator){
                    console.log('modify organizers')
                }else{
                    console.log('non puoi')
                }
            }
        }
    }

    const logout = async () => {
        if (tokenData !== null) {
            const response = await logoutUser(tokenData)
            if (typeof response === 'boolean') {
                if (response) {
                    navigate('/')
                } else {
                    console.log('error')
                }
            } else {
                navigate('/error', {state: {error: response}})
            }
        }
    }

    const goBack = () => {
        navigate(-1)
    }

    const handleUserAdd = (user: any) => {
        if (!selectedUsers.includes(user)) {
            setSelectedUsers([...selectedUsers, user])
        }
    }

    const handleDelete = (userToDelete: any) => () => {
        console.log('chiamo questa funzione')
        setSelectedUsers((users: any[]) => users.filter((user) => user !== userToDelete));
    };


    return (
        <>
            <Grid item xs={12} justifyContent="center" alignItems="center">
                <Box sx={{flexGrow: 1}}>
                    <AppBar position="fixed" sx={{backgroundColor: '#3d4347'}}>
                        <Toolbar>
                            <IconButton
                                size="small"
                                edge="start"
                                aria-label="menu"
                                sx={{mr: 2}}
                            >
                                <ArrowBackIcon onClick={goBack} sx={{fontSize: '3rem', color: 'white'}}/>
                            </IconButton>
                            <Typography variant="h6" component="div" sx={{flexGrow: 1}}
                                        style={{
                                            justifyContent: 'center',
                                            color: 'white',
                                            textAlign: 'center',
                                            fontSize: '1.8rem',
                                        }}>Dettagli initiativa
                            </Typography>
                            <Button sx={{color: 'white', backgroundColor: 'red'}}
                                    onClick={logout}>
                                logout
                            </Button>
                        </Toolbar>
                    </AppBar>
                </Box>
            </Grid>
            <Grid container display="flex" justifyContent="flex-start" alignItems="center" sx={{width: '100%', marginTop:'65px'}} spacing={3}>

                <Grid item xs={12} >
                    <Typography
                                style={{
                                    justifyContent: 'center',
                                    color: 'white',
                                    textAlign: 'center',
                                    fontSize: '1.8rem',

                                }}>{initiative.name}
                    </Typography>
                </Grid>
                <Grid item xs={12}>
                    <Typography sx={{color: InitiativeTypeColor[initiativeType], fontWeight:'bold', fontSize:'1.2rem'}}>{initiative.type}</Typography>
                </Grid>

                <Grid item xs={12} >
                        <Box sx={{width: '100%', height: '100%',border: '2.5px solid #feac0d',}}>
                            <CssTextField
                                sx={{backgroundColor: 'white', width: '100%', height: '100%',}}
                                value={description}
                                maxRows={5}
                                onChange={handleChange}
                                multiline
                                InputProps={{
                                    readOnly: isReadOnly(),
                                }}/>
                        </Box>
                </Grid>

                <Grid item xs={12}>
                    <Box sx={{ width: 1 }}>
                        <Box display="grid" gridTemplateColumns="repeat(12, 1fr)" gap={2}>
                            <Box gridColumn="span 6">
                                <LocalizationProvider dateAdapter={AdapterDayjs}>
                                    <DatePicker
                                        inputFormat= "DD/MM/YYYY"
                                        label="Data inizio"
                                        value={startDate}
                                        onChange={(newValue) => {
                                            handleStartDateChanged(newValue);
                                        }}
                                        renderInput={(params) => <CssTextField {...params} sx={{
                                            input: {color: 'white'},
                                            style: {color: 'white'}
                                        }}/>}
                                    />
                                </LocalizationProvider>
                            </Box>
                            <Box gridColumn="span 6">
                                <LocalizationProvider dateAdapter={AdapterDayjs}>
                                    <DatePicker
                                        inputFormat= "DD/MM/YYYY"
                                        label="Data fine"
                                        value={endDate}
                                        onChange={(newValue) => {
                                            handleEndDateChanged(newValue);
                                        }}
                                        renderInput={(params) => <CssTextField {...params} sx={{
                                            input: {color: 'white'},
                                            style: {color: 'white'}
                                        }}/>}
                                    />
                                </LocalizationProvider>
                            </Box>
                        </Box>
                    </Box>
                </Grid>

                <Grid item xs={12} >
                    <Typography sx={{textAlign: "center"}} >Creatore: </Typography>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center" >
                    <Chip sx={{backgroundColor: '#feac0d'}}
                                                icon={<AccountCircleIcon sx={{color: 'white'}}/>}
                                                label={initiative.idCreator}/>
                </Grid>
                <Grid item xs={12} >
                    <Typography sx={{textAlign: "center"}}>Organizzatori: </Typography>
                </Grid>
                <Grid item xs={12}>
                    {
                        selectedUsers.map((organizer: string) => {
                            return (
                                <Grid item display="flex" xs={12} justifyContent="center" alignItems="center">
                                    <Chip sx={{backgroundColor: '#feac0d', marginTop: '10px', textAlign:'center'}}
                                          icon={<AccountCircleIcon sx={{color: 'white'}}/>}
                                          label={organizer}
                                          onDelete={organizer===user.email|| user.email===initiative.idCreator? handleDelete(organizer): undefined}
                                    />
                                </Grid>)
                        })
                    }
                </Grid>
                {
                    user && user.email===initiative.idCreator && !isVisitor?
                        <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                            <FormControl sx={{width: '70%'}}>
                                <CssTextField sx={{input: {color: 'white'}, style: {color: 'white'}}}
                                              select
                                              label='Organizzatori'>
                                    {
                                        usersList ? usersList.map((user) => {
                                            return <MenuItem onClick={() => {
                                                handleUserAdd(user)
                                            }} value={user}>{user}</MenuItem>
                                        }): null
                                    }
                                </CssTextField>
                            </FormControl>
                        </Grid> : null
                }
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    {isVisitor ? null : userIsOrganizer() ?
                        <><Button style={{
                            borderRadius: 35,
                            backgroundColor: "#92d36e",
                            padding: "18px 36px",
                            fontSize: "15px",
                        }}
                              onClick={handleModify}    variant="contained">
                            Modifica
                        </Button><Button style={{
                            borderRadius: 35,
                            backgroundColor: "red",
                            padding: "18px 36px",
                            fontSize: "15px",
                            marginLeft: '30px'
                        }}
                                         variant="contained">
                            Cancella
                        </Button></>
                        :
                        <Button style={{
                            borderRadius: 35,
                            backgroundColor: "#92d36e",
                            padding: "18px 36px",
                            fontSize: "15px",
                        }}
                                variant="contained">
                            Partecipa
                        </Button>
                    }

                </Grid>
            </Grid>



        </>

    );
}

export default InitiativeDetails;
