import React, {useEffect, useState} from "react";
import {Navigate, useLocation} from "react-router-dom";
import {Button, Divider, Grid, Typography} from "@mui/material";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemText from "@mui/material/ListItemText";
import AccountCircleIcon from '@mui/icons-material/AccountCircle';


const UsersAdministration = () => {
    const location = useLocation()
    const [usersList, setUsersList] = useState<any[]>(['utente1', 'utente2', 'utente3', 'utente4', 'utente5', 'utente6', 'utente7', 'utente8', 'utente9', 'utente10', 'utente11', 'utente12'])
    const [selectedUser, setSelectedUser] = useState<any>(null)
    const token = location.state?.token
    const isAdmin = location.state?.isAdmin

    useEffect(() => {
        if (isAdmin) {
            console.log('sei admin e prelevo la lista di tutti gli utenti')
        }
    })


    if (token === undefined || isAdmin === undefined) {
        console.log('non sei loggato')
        return <Navigate to='/'/>
    }
    if (!isAdmin) {
        console.log('non sei admin')
        return <Navigate to='/'/>
    }

    const handleSilenceUser = () => {
        console.log('SILENZIO L\'UTENTE' + selectedUser)
    }

    const handleDeleteUser = () => {
        console.log('ELIMINO L\'UTENTE' + selectedUser)
    }

    const handleUserSelection = (user: any) => {
        setSelectedUser(user)
    }

    const userListItemStyle = (user: any) => {
        if (user === selectedUser) {
            return '#92d36e'
        } else {
            return '#ffffff'
        }
    }


    return (
        <>
            <Grid container spacing={2} justifyContent="center" alignItems="center">
                <Grid item xs={12} display="flex" justifyContent="center" alignItems="center">
                    <Typography style={{color: 'white', textAlign: 'center', fontSize: '3rem'}}>Amministrazione
                        utenti</Typography>
                </Grid>
                <Grid item xs={12} display="flex" justifyContent="center" sx={{marginTop: '55px'}}>
                    <List sx={{
                        width: '90%',
                        position: 'relative',
                        overflow: 'auto',
                        maxHeight: 500,
                    }}>
                        {usersList.map((value, index) => {
                            return (
                                    <ListItem key={value} style={{color: userListItemStyle(value)}}>
                                        <AccountCircleIcon/>
                                        <ListItemButton onClick={() => {
                                            handleUserSelection(value);
                                        }}>
                                            <ListItemText primary={value}
                                                          secondaryTypographyProps={{color: userListItemStyle(value)}}
                                                          secondary={'email di ' + value}/>
                                        </ListItemButton>
                                    </ListItem>
                            );
                        })}


                    </List>
                </Grid>
                <Grid item xs={3} display="flex" justifyContent="center">
                    <Button style={{
                        borderRadius: 35,
                        backgroundColor: "red",
                        padding: "10px 20px",
                        fontSize: "18px"
                    }}
                            variant="contained"
                            onClick={() => {
                                handleDeleteUser();
                            }}>
                        {'Elimina utente'}
                    </Button>
                </Grid>
                <Grid item xs={3} display="flex" justifyContent="center">
                    <Button style={{
                        marginTop: '15px',
                        borderRadius: 35,
                        backgroundColor: '#feac0d',
                        padding: "18px 36px",
                        fontSize: "15px"
                    }}
                            variant="contained"
                            onClick={() => {
                                handleSilenceUser();
                            }}>
                        {'Silenzia utente'}
                    </Button>
                </Grid>
            </Grid>
        </>
    );
}

export default UsersAdministration;
