import React, {useEffect, useState} from "react";
import {Button, Grid, ListItemSecondaryAction, ListItemText, Typography} from "@mui/material";
import {useLocation, useNavigate} from "react-router-dom";
import ListItem from "@mui/material/ListItem";
import AdminPanelSettingsIcon from "@mui/icons-material/AdminPanelSettings";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import ListItemButton from "@mui/material/ListItemButton";
import List from "@mui/material/List";


const Error = () => {
    const [errorReason, setErrorReason] = useState<string>('');
    const location= useLocation()
    const error= location.state.error
    const navigate= useNavigate()
    const [infoList, setInfoList]=useState<string[]>([])

    const labels= ['']

    console.log(error)
    useEffect(() => {
        if(infoList.length===0){
            splittedRequest()
        }
    })
    // "http://localhost:8080/userAPI/v1/user/get/michelestaf@gmail.com"
    const splittedRequest=()=>{
        const path= error.requestedPath.split('/')
        const returnValue=[]
        let pathtoprint=''
        for(let value=0; value<path.length; value++) {
            if (path[value] !== '') {
                if(value===2 || value===3){
                    returnValue.push(path[value])
                }
                if(value>4){
                    pathtoprint+= path[value] + '/'
                }
            }
        }
        returnValue.push(pathtoprint)
        console.log(returnValue)
        setInfoList(returnValue)
    }

    return (
            <Grid container direction={'column'}>
                <Grid item display="flex" justifyContent="center" alignItems="center">
                    <Typography variant={'h2'}>
                        ATTENZIONE
                    </Typography>
                </Grid>
                <Grid item display="flex" justifyContent="center" alignItems="center">
                    <Typography variant={'h5'}>Error code: {error.code}</Typography>
                </Grid>
                <Grid item display="flex" justifyContent="center" alignItems="center">
                    <h4>Method:</h4> {error.method}
                </Grid>
                <Grid xs={12} item display="flex" justifyContent="center" alignItems="center" sx={{margin:'auto'}}>
                    <List>

                        {
                            infoList ?
                                infoList.map((value, index)=>{
                                    return (
                                        <ListItem>{value}</ListItem>
                                    )
                                })
                                : null
                        }
                    </List>
                    {
                    }
                </Grid>

                <Button variant="outlined" color="error" onClick={()=>{navigate('/')}}>TORNA AL LOGIN</Button>
            </Grid>
    );
}

export default Error;
